    private static int star1(int right, int down) {
        int x = 0;
        int sum = 0;
        for (int i = 0; i < input.size(); i+= down) {
            String row = input.get(i);
            x = x % row.length();
            char c = row.charAt(x);
            if (c == '#') {
                sum++;
            }
            x += right;
        }
        return sum;
    }

    private static int star2() {
        return star1(1, 1) *
                star1(3, 1) *
                star1(5, 1) *
                star1(7, 1) *
                star1(1, 2);
    }
